mapper.update:
UPDATE directories d


        name = #{name},

        parent_id = #{parentId},

WHERE d.id = #{directoryId}
  AND d.kb_id = #{kbId}
  AND EXISTS (
      SELECT 1 
      FROM knowledge_bases kb 
      WHERE kb.id = #{kbId} 
        AND kb.user_id = #{userId}
  )

mapper.insert:
INSERT INTO directories (id, kb_id, parent_id, name, post_id, sort_num)
SELECT
    #{id},
    #{kbId},
    #{parentId},
    #{name},
    #{postId},
    -- 计算排序序号，使用当前子目录的最大sortNum加1
    COALESCE(
            (SELECT MAX(sort_num) + 1
             FROM directories
             WHERE kb_id = #{kbId}
               AND (parent_id = #{parentId} OR (parent_id IS NULL AND #{parentId} IS NULL))
            ),
            1
    )
WHERE EXISTS (
    -- 验证知识库所有权
    SELECT 1
    FROM knowledge_bases kb
    WHERE kb.id = #{kbId}
      AND kb.user_id = #{userId}
)
  AND (
    -- 验证parent_id的kb_id一致性（如果parent_id不为空）
    #{parentId} IS NULL
        OR EXISTS (
        SELECT 1
        FROM directories d
        WHERE d.id = #{parentId}
          AND d.kb_id = #{kbId}
    )
    )

mapper.select:
SELECT d.*
FROM directories d
WHERE d.id = #{id}
  AND d.kb_id = #{kbId}
  AND EXISTS (
      SELECT 1
      FROM knowledge_bases kb
      WHERE kb.id = #{kbId}
        AND kb.user_id = #{userId}
  )

SELECT *
FROM directories
WHERE kb_id = #{kbId}
  AND (

          parent_id = #{parentId}


          parent_id IS NULL

  )
ORDER BY sort_num
