mapper.select:
SELECT

FROM reply
WHERE par_comment_id = #{parCommentId}

    AND (
    like_count < #{lastLikeCount}
    OR (like_count = #{lastLikeCount} AND created_at < #{lastCreatedAt})
    OR (like_count = #{lastLikeCount} AND created_at = #{lastCreatedAt} AND id < #{lastId})
    )

ORDER BY like_count DESC, created_at DESC,id DESC
LIMIT 10

SELECT

FROM reply
WHERE post_comment_id = #{postCommentId}

    AND (
    like_count < #{lastLikeCount}
    OR (like_count = #{lastLikeCount} AND created_at < #{lastCreatedAt})
    OR (like_count = #{lastLikeCount} AND created_at = #{lastCreatedAt} AND id < #{lastId})
    )

ORDER BY like_count DESC, created_at DESC,id DESC
LIMIT 10
